 var isValid = function(str){
    var map = {
      "{":"}",
      "[":"]",
      "(":")"
    }
    if(str.length % 2 === 1) return false;
    var res = [];
    for(var i = 0;i < str.length;i++){
      if(map[str[i]]){
        res.push(str[i]);
      } else {
        var a = res[res.length-1];
        if(map[a] == str[i]) {
          res.pop()
        } else {
          return false;
        } 
      }
    }
    return res.length===0;
  }
